#include <stdio.h>
#include <openssl/bn.h>

void printBN(char *msg, BIGNUM *a)
{
    char *number_str_a = BN_bn2hex(a);
    printf("%s %s\n", msg, number_str_a);
    OPENSSL_free(number_str_a);
}

int main()
{
    BN_CTX *ctx = BN_CTX_new();
    BIGNUM *n = BN_new(); //modulus
    BIGNUM *e = BN_new(); //public key
    BIGNUM *M = BN_new(); //server certificate
    BIGNUM *C = BN_new(); 
    BIGNUM *S = BN_new(); //hash of the certificate body

    // assign values
    //modulus
    BN_hex2bn(&n, "");
    BN_dec2bn(&e, "65537"); //this hex value equals to decimal 65537
	//server cerf
    BN_hex2bn(&M, ""); 
    //hash of the certificate body
    BN_hex2bn(&S, "");

    // decryption S: S^e mod n = (M^d mod)^e mod n = M
    BN_mod_exp(C, S, e, n, ctx);

    if (BN_cmp(C, M) == 0) //if compare is equal
    {
        printf("Valid Signature! \n"); //signature is valid
    }
    else //if comparison is not equal or else 
    {
        printf("Verification fails! \n"); //signature is not valid
    }
    return 0; //end
}